import React, {Fragment, useState} from 'react';
import {NavLink, withRouter}  from 'react-router-dom'

import TimelinePage from "./timelinePage"
import ProjectsPage from "./projectsPage"

import { ReactComponent as MenuIcon} from '../icons/menu.svg';
import { ReactComponent as HomeIcon} from '../icons/home.svg';
import { ReactComponent as ProjectsIcon} from '../icons/projects.svg';
import { ReactComponent as SkillsIcon} from '../icons/skills.svg';
import { ReactComponent as KnowledgeIcon} from '../icons/knowledge.svg';
import { ReactComponent as InterestsIcon} from '../icons/interests.svg';

import "../public/styles/style.css";

import logo from '../public/images/Logo.png';
import {CSSTransition} from 'react-transition-group';

function getNavLinkClass(path){
  return this.props.location.pathname === path ? 'active' : '';
}

function Navbar() {
    return (
      <Navbarf>
        <NavItem icon={<MenuIcon/>} >
          <DropdownMenu />
        </NavItem>
        <h1 className="Name">Anthony Rich</h1>
        <img className="logo" src={logo} />
      </Navbarf>

    );
}

function Navbarf(props){
  return (
    <nav className='navbar'>
      <ul className="navbar-nav">{ props.children }</ul>
    </nav>
  );
}

function DropdownMenu(){

  const [activeMenu, setActiveMenu] = useState('main');

  function DropdownItem(props){
    return(

    <NavLink to={props.url} className="menu-item">
      <span className="icon-button">{props.leftIcon}</span>
      {props.children}
    </NavLink>
    );
  }

  return(
    <div className="dropdown">
      <CSSTransition in={activeMenu === 'main'} 
      unmountOnExit 
      timeout={500}
      classNames="menu-primary">
        <div className="menu">
        <DropdownItem leftIcon={<HomeIcon/>} url="TimelinePage">
            <p style={{marginLeft: "10px"}}>Inicio</p>
        </DropdownItem>
        <DropdownItem leftIcon={<ProjectsIcon/>} url="ProjectsPage">
          <p style={{marginLeft: "10px"}}>Proyectos</p>
        </DropdownItem>
        <DropdownItem leftIcon={<InterestsIcon/>}>
          <p style={{marginLeft: "10px"}}>Intereses</p>
        </DropdownItem>
        <DropdownItem leftIcon={<SkillsIcon/>}>
          <p style={{marginLeft: "10px"}}>Habilidades</p>
        </DropdownItem>
        <DropdownItem leftIcon={<KnowledgeIcon/>}>
          <p style={{marginLeft: "10px"}}>Conocimientos</p>
        </DropdownItem>
        </div>
      </CSSTransition>
    </div>
  );
}

function NavItem(props){

  const[open, setOpen] = useState(false);

  return (
    <li className="nav-item">
      <a href="#" className="icon-button" onClick={() => setOpen(!open)}>
        {props.icon}
      </a>
      {open && props.children}
    </li>
  );
}


export default Navbar;
